sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
var whatuneeded = g_form.getValue("u_what_needed");

	//Clear the options from the what u needed field
	g_form.clearOptions("u_what_needed");

	//if the new value of the Request type field value is hr add 2 new hr's and other to the 
	//what u needed field
	if (newValue == 'hr') {
		g_form.addOption('u_what_needed','hr','Human Resource1');
		g_form.addOption('u_what_needed','hr','Human Resource2');
		g_form.addOption('u_what_needed','other','other');
	}

	//if the new value of the "Request type" field value is facilites  
	//add 2 new facilites's and other to the "what u needed" field
	if (newValue == 'facilites') {
		g_form.addOption('u_what_needed','facilites','facilites1');
		g_form.addOption('u_what_needed','facilites','facilites2');
		g_form.addOption('u_what_needed','other','other');
	}

	//if the new value of the "Request type" field value is legal  
	//add 2 new legal's and other to the "what u needed" field
	if (newValue == 'legal') {
		g_form.addOption('u_what_needed','legal','legal1');
		g_form.addOption('u_what_needed','legal','legal2');
		g_form.addOption('u_what_needed','other','other');		
	}

	//if the form is loading or it is not a new record, then set the previous u_what_needed value  
	//before its load
	if (isLoading || !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatuneeded);
	}
   //Type appropriate comment here, and begin script below

}
